package com.handson.soul.advice;import io.github.resilience4j.ratelimiter.RequestNotPermitted;import jakarta.servlet.http.HttpServletRequest;import lombok.extern.slf4j.Slf4j;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;@Slf4j@ControllerAdvicepublic class CustomExceptionHandler {    @ExceptionHandler(RequestNotPermitted.class)    public ResponseEntity<Object> handleRequestNotPermitted(RequestNotPermitted ex, HttpServletRequest request) {        log.info("Request to Path '{}' is blocked dur to rate limiting. {}", request.getRequestURI(), ex.getMessage());        return new ResponseEntity<>("Too many requests", HttpStatus.TOO_MANY_REQUESTS);    }}