package com.handson.soul.components;import io.github.resilience4j.circuitbreaker.CircuitBreakerRegistry;import io.github.resilience4j.circuitbreaker.CircuitBreaker;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Component;@Componentpublic class CircuitBreakerEventLoggerComponent {    @Autowired    public CircuitBreakerEventLoggerComponent(CircuitBreakerRegistry registry) {        CircuitBreaker cb = registry.circuitBreaker("myService");        cb.getEventPublisher()                .onStateTransition(event -> System.out.println("State transition: " + event.getStateTransition()))                .onError(event -> System.out.println("Error record: " + event.getThrowable()))                .onSuccess(event -> System.out.println("Success: " + event.getElapsedDuration()))        ;    }}